class hoursDuration{constructor(t){this.hour=t}isNegative(t){return t||(t=this.hour),"-"==t.toString()[0]}toMinutes(t){t||(t=this.hour);let e=t.replace("-","").split(":"),s=60*parseInt(e[0])+parseInt(e[1]);return(this.isNegative(t)?"-":"")+s}ofMinutes(t){let e=t.toString().replace("-",""),s=(parseInt(e)/60).toFixed();return(e=parseInt(e)-60*s)<0&&(e=parseInt(e)+60,s=parseInt(s)-1),(this.isNegative(t)?"-":"")+(s<10?"0":"")+s+":"+(e<10?"0":"")+e}add(t){let e=parseInt(this.toMinutes())+parseInt(this.toMinutes(t));return e=this.ofMinutes(e)}remove(t){let e=parseInt(this.toMinutes())-parseInt(this.toMinutes(t));return e=this.ofMinutes(e)}}
